const fechaActualTexto = $input.first().json['Fecha actual'];
const fechaNuevaTexto = $input.first().json['Fecha nueva'];

// Función para sumar 1 hora y mantener formato YYYY-MM-DDTHH:mm:ss-05:00
function sumarUnaHora(fechaTexto) {
  const fecha = new Date(fechaTexto);
  fecha.setHours(fecha.getHours() + 1); // Sumar 1 hora
  const offset = -5; // Para la zona horaria de Perú (-05:00)

  // Ajustar la fecha para mantener la zona horaria de -05:00
  const hora = fecha.getHours() + offset;
  const minutos = fecha.getMinutes().toString().padStart(2, '0');
  const segundos = fecha.getSeconds().toString().padStart(2, '0');

  // Formatear la fecha en el formato deseado
  return `${fecha.getFullYear()}-${(fecha.getMonth() + 1).toString().padStart(2, '0')}-${fecha.getDate().toString().padStart(2, '0')}T${hora.toString().padStart(2, '0')}:${minutos}:${segundos}-05:00`;
}

const fechaActualMasUna = sumarUnaHora(fechaActualTexto);
const fechaNuevaMasUna = sumarUnaHora(fechaNuevaTexto);

return [
  {
    json: {
      "Fecha actual original": fechaActualTexto,
      "Fecha actual +1h": fechaActualMasUna,
      "Fecha nueva original": fechaNuevaTexto,
      "Fecha nueva +1h": fechaNuevaMasUna
    }
  }
];
